import React,{useState,useEffect} from 'react';
import axios from 'axios';
import './App.css';

function App() {

let [line, setLine ] = useState([])
let [hour, setHour] = useState('1501')
let [data, setData] = useState('')

function getLine(e){
  //https://mocline.herokuapp.com/
  axios.get(`https://mocline.herokuapp.com/${e}`,{
    method: 'GET',
    mode: 'no-cors'
  })
  .then(function (response) {
    // handle success
    setData(response.data.toString())
  })
  .catch(function (error) {
    // handle error
    console.log(error);
  })
  .finally(function () {
    // always executed
  });
}



useEffect(()=>{
  axios.get('https://mocline.herokuapp.com/linhas',{
    method: 'GET',
    mode: 'no-cors'
  })
  .then(function (response) {
    // handle success
    setLine(Object.keys(response.data))
    //console.log(response.data);
  })
  .catch(function (error) {
    // handle error
    console.log(error);
  })
  .finally(function () {
    // always executed
  });
})

const handleSubmit = (evt) => {
  evt.preventDefault();
  alert(hour)
}


  return (
    <div className="App">
      <header className="App-header">
        <form onSubmit={handleSubmit}>
        <select onChange={(e)=>{
          setHour(e.target.value);
        }}>
          {line.map((e) =>{
          return <option value={e} >{e}</option>
           })}
        </select>
        <button type="submit" value="enviar">buscar</button>
        </form>


      </header>
    </div>
  );
}

export default App;
