import React,{useState,useEffect} from 'react';
import axios from 'axios';
import './App.css';

function App() {

let [line, setLine ] = useState([])


function getLine(e){
  //https://mocline.herokuapp.com/
  axios.get(`https://mocline.herokuapp.com/${e}`,{
    method: 'GET',
    mode: 'no-cors'
  })
  .then(function (response) {
    // handle success
    console.log(response.data);
  })
  .catch(function (error) {
    // handle error
    console.log(error);
  })
  .finally(function () {
    // always executed
  });
}



useEffect(()=>{
  axios.get('https://mocline.herokuapp.com/linhas',{
    method: 'GET',
    mode: 'no-cors'
  })
  .then(function (response) {
    // handle success
    setLine(Object.keys(response.data))
    //console.log(response.data);
  })
  .catch(function (error) {
    // handle error
    console.log(error);
  })
  .finally(function () {
    // always executed
  });
})



  return (
    <div className="App">
      <header className="App-header">
        <ul>
          {line.map((e) =>{
           return <li onClick={()=>{
             getLine(e)
           }}>{e}</li>
          })}
        </ul>
      </header>
    </div>
  );
}

export default App;
