import React,{useState,useEffect} from 'react';
import './App.css';

function App() {

let [line, setLine ] = useState({})


async function getLine() {
 const req = await fetch('https://api.ipify.org?format=json')
 return req.json()

}

useEffect(()=>{
  const pro = getLine()
  pro.then((e) =>{
    setLine(e.ip)
  })
})



  return (
    <div className="App">
      <header className="App-header">
        <h1>
          {line}
        </h1>
      </header>
    </div>
  );
}

export default App;
